pragma solidity ^0.7.5;

import "./Auction.sol";

contract DutchAuction is Auction{

	//DutchAuction will need additional parameters like the rate at which the price is lowered 
	//How much is the value being decremented by and how often is the decrement taking place

	uint256 public rate;

	constructor (uint _biddingTime, address payable _owner, string memory _itemName, uint _startBid, uint _minBid) public {
		auctionOwner = _owner;
		auctionStart = block.timestamp; 
		auctionEnd = auctionStart + _biddingTime * 1 minutes;
		minPrice = _minBid;
		rate = _startBid;
		ongoingAuction = true;
	}

	function bid() public payable ongoing_auction override returns (bool) {
		require(msg.value >= rate * (block.timestamp - auctionStart));

		highestBid = msg.value;
		highestBidder = msg.sender;

		ongoingAuction = false;

		emit BidEvent(highestBidder, highestBid);
		return true;
	}

}
